      });
      const json = await response.json();
      setUsers(json);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };




  // GET all Tasks
  const getTasks = async () => {
    //API call
    const response = await fetch(`${host}/api/tasks/fetchalltasks`, {
