  const newAssignedMembersData = {};
  tasks.forEach((task) => {
    newAssignedMembersData[task._id] = task.assignedMembers;
  });

  // Update the assignedMembersData state with the new data
  setAssignedMembersData(newAssignedMembersData);
}, [tasks]);


  return (
    <TaskContext.Provider
      value={{ tasks, addTask, deleteTask, editTask, getTasks, users,  fetchAllTasks, fetchAllUsers, assignedMembersData}}
    >
      {props.children};
    </TaskContext.Provider>
